package StarPatternCode;

public class Printnmber_oattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  
		for(int i=1;i<=5;i++) {
			
			for(int j=1;j<=i;j++) {
				System.out.print(i);     //if you use  i ki jageh j -1,12,123,1234,12345
			}
			System.out.println();
		}
		
	}

}
